package ru.artsybashev.bacgame;

import java.io.IOException;

public class App {
    private static String filepath;
    private static int numOfDigits = 4;
    public static void main(String[] args) throws IOException {
        //  проверяем первый аргумент командной строки (число разрядов)
        if (Checkout.checkArgs0(args)) {
            numOfDigits = Checkout.checkAndSetValueOfDigits(args[0]); // понимаю, что метод должен делать что-то одно, но...
        }

        filepath = ServiceLib.getFilePath(args);


        //System.out.println("Log файл находится в директории: " + filepath);
        BullsAndCowsGame game = new BullsAndCowsGame(numOfDigits, filepath);
        game.startGame();
    }
}
